import React, {useState} from 'react'
import {Link} from "react-router-dom";

function RegisterPage() {
    const [error = "", setError] = useState("");
    function emailHandler() {
        setError("Please Enter an Email");
    }
    function usernameHandler() {
        setError("Please enter a user name");
    }
    function passHandler() {
        setError("Please enter a password");
    }
    function handleClick(e){
        e.preventDefault();
        let error = document.getElementById("registerError");
        const email = document.getElementById("email").innerText;
        const username = document.getElementById("username").innerText;
        const password = document.getElementById("password").innerText;
        if (email === "" || username === "" || password === "")
        {
            if(email === "")
            {
                error.innerHTML = "Please Enter an Email";
                console.log(email);
            }
            else if(username === "")
            {
                error.innerHTML = "Please Enter a Username";
            }
            else
            {
                error.innerHTML = "Please Enter a Password";
            }
        }
        else if (password.length < 16)
        {
            error.innerHTML = "Please write a password with 16 letter or more";
        }


    }

    return (
        <div>
            <button onClick={emailHandler}>Increase</button>
            <button onClick={usernameHandler}>Decrease</button>
            <button onClick={passHandler}>Restart</button>
            <div>{error}</div>

            <form id="registerpanel">
                <h1 id="litheader">Register</h1>
                <div className="inset">
                    <p>
                        <input type="text" name="email" id="email" placeholder="Email"/>
                    </p>
                    <p>
                        <input type="text" name="username" id="username" placeholder="User Name"/>
                    </p>
                    <p>
                        <input type="password" name="password" id="password" placeholder="Password"/>
                    </p>
                </div>
                <div className="p-container" id="registerError"></div>
                <p className="p-container">
                    <button onClick={handleClick}>Register</button>
                </p>
            </form>

        </div>
    );
} export default RegisterPage;
