import React, {useRef, useState} from 'react'
import {useNavigate} from "react-router-dom";

export default function EmailValidation() {
    const [error = "", setError] = useState("");
    const navigate = useNavigate();
    const emailRef = useRef(null);

    function handleClick(e){
        e.preventDefault();
        let username = usernameRef.current.value;
        let password = passwordRef.current.value;

        if (username === "" || password === "")
        {
            if(username === "")
            {
                setError('Please Enter a Username');
            }
            else
            {
                setError('Please Enter a Password');
            }
        }
        else if (password.length < 10)
        {
            setError('Please write a password with 10 letter or more');
        }
        else
        {
            // check the user in the db if exist do and write what if not
            setError('');
            navigate('/reports');
        }
    }


    return (
        <div>
            <form id="emailValidationpanel">
                <h1 id="litheader">Email Validation</h1>
                <div className="inset">
                    <p>
                        <input
                            type="text"
                            name="email"
                            id="email"
                            placeholder="Email"
                            ref={emailRef}
                        />
                    </p>
                </div>
                <div className="p-container" id="emailValidationError">{error}</div>
                <p className="p-container" id="button">
                    <button onClick={handleClick}>Get Email</button>
                </p>
            </form>
        </div>
    )
}
