import React, {useState} from 'react'
import {Link} from "react-router-dom";

function RegisterPage() {
    const [error = "", setError] = useState("");
    function emailHandler() {
        setError("Please Enter an Email");
    }
    function usernameHandler() {
        setError("Please enter a user name");
    }
    function passHandler() {
        setError("Please enter a password");
    }
    function handleClick(e){
        e.preventDefault();
        let error = document.getElementById("registerError");
        let email = document.getElementById("email");
        let username = document.getElementById("username");
        let password = document.getElementById("password");

        let array = [email, username, password];
        array.forEach(element => {
            if(element.innerHTML === ""){
                setError("Please enter a password");
                console.log(element);
            }
        })

        // if (email.innerText === "" || username.innerText === "" || password.innerText === "")
        // {
        //     if(email.innerText === "")
        //     {
        //         error.innerText = "Please Enter an Email";
        //         console.log(email.innerHTML);
        //     }
        //     else if(username.innerText === "")
        //     {
        //         error.innerHTML = "Please Enter a Username";
        //     }
        //     else
        //     {
        //         error.innerHTML = "Please Enter a Password";
        //     }
        // }
        // else if (password..innerText.length < 16)
        // {
        //     error.innerHTML = "Please write a password with 16 letter or more";
        // }


    }

    return (
            <form id="registerpanel">
                <h1 id="litheader">Register</h1>
                <div className="inset">
                    <p>
                        <input type="text" name="email" id="email" placeholder="Email"/>
                    </p>
                    <p>
                        <input type="text" name="username" id="username" placeholder="User Name"/>
                    </p>
                    <p>
                        <input type="password" name="password" id="password" placeholder="Password"/>
                    </p>
                </div>
                <div className="p-container" id="registerError">{error}</div>
                <p className="p-container">
                    <button onClick={handleClick}>Register</button>
                </p>
            </form>
    );
} export default RegisterPage;
