import React from 'react'
import ReactDOM from "react-dom/client";

export default function MainPage() {
    function loginFunction() {
        const root = document.getElementById('root');
        const indexpanel = document.getElementById('indexpanel');
        root.removeChild(indexpanel);
    }

    return (
        <div>
            <form id="indexpanel">
                <h1 id="litheader">Welcome to Communication LTD</h1>
                <br />
                <p className="p-container">
                    <input type="submit" id="login" value="Login" />
                </p>
                <p className="p-container">
                    <input type="submit" id="register" value="Register" />
                </p>
            </form>
        </div>
    );

    // Set up the event listener on the form element with the id of "indexpanel"
    document.getElementById('indexpanel').addEventListener('submit', (event) => {
        event.preventDefault(); // prevent form from submitting
        const target = event.target;
        if (target.id === 'login') {
            loginFunction();
        }
    });
}
