import React, {useRef, useState} from 'react'
import {useNavigate} from "react-router-dom";

export default function ChangePassword() {
    const [error = "", setError] = useState("");
    const navigate = useNavigate();
    const oldPasswordRef = useRef(null);
    const newPasswordRef = useRef(null);

    function handleClick(e){
        e.preventDefault();
        let oldPassword = oldPasswordRef.current.value;
        let newPassword = newPasswordRef.current.value;

        if (oldPassword === "" || newPassword === "")
        {
            if(oldPassword === "")
            {
                setError('Please Enter an old password');
            }
            else
            {
                setError('Please Enter an new password');
            }
        }
        else
        {
            // check the code in the db if exist and equal
            //write what will happen if not equal
            setError('');
            alert("The password changed successfully");
            navigate('/login');
        }
    }


    return (
        <div>
            <form id="changepasswordpanel">
                <h1 id="litheader">Change Password</h1>
                <div className="inset">
                    <p>
                        <input type="text" name="oldPassword" id="oldPassword" placeholder="Old Password" ref={oldPassword}/>
                    </p>
                    <p>
                        <input type="text" name="newPassword" id="newPassword" placeholder="New Password" ref={newPassword}/>
                    </p>
                </div>
                <div className="p-container" id="changePasswordError">{error}</div>
                <p className="p-container">
                    <button onClick={handleClick}>Change Password</button>
                </p>
            </form>
        </div>
    )
}
