            setError('');
            navigate('/cv');
        }
    }


    return (
        <div>
            <form id="codeValidationpanel">
                <h1 id="litheader">Code Validation</h1>
                <div className="inset">
                    <p>
                        <input
                            type="text"
                            name="code"
                            id="code"
                            placeholder="Code"
                            ref={codeRef}
                        />
                    </p>
                </div>
                <div className="p-container" id="codeValidationError">{error}</div>
                <p className="p-container" id="button">
                    <button
                        onClick={handleClick}
                    >Validate Code</button>
                </p>
            </form>
        </div>
    )
}
